+        const email = this.querySelector('input[name="email"]').value;
+        const destination = this.querySelector('select[name="destination"]').value;
+        if (name && email && destination) {
+            alert(`Thank you, ${name}! Your booking to ${destination} is confirmed (mock). We'll contact you at ${email}.`);
+            this.reset();
+        } else {
+            alert('Please fill in all fields.');
+        }
+    });
+});
+
+// Simple parallax effect on home section
+window.addEventListener('scroll', function () {
+    const homeSection = document.querySelector('#home');
+    const scrollPosition = window.pageYOffset;
+    homeSection.style.backgroundPositionY = `${scrollPosition * 0.5}px`;
+});
+
+// Fade-in animation for sections
+const sections = document.querySelectorAll('section');
+const options = {
+    threshold: 0.1
+};
+
+const observer = new IntersectionObserver((entries) => {
+    entries.forEach(entry => {
+        if (entry.isIntersecting) {
+            entry.target.classList.add('fade-in');
+        }
+    });
+}, options);
+
+sections.forEach(section => {
+    section.classList.add('fade-section');
+    observer.observe(section);
+});
